package nl.mranderson.pokefeeds;import android.support.v7.widget.RecyclerView;import com.rssreader.RssItem;import java.util.List;import nl.mranderson.pokefeeds.network.PokeNewsController;public class VideoFragment extends MediaFragment {    private VideoAdapter mAdapter;    private PokeNewsController pokeNewsController;    private String CONTENT_URL;    @Override    protected void getData() {        CONTENT_URL = "https://www.googleapis.com/youtube/v3/search?part=snippet&fields=items(id(videoId),snippet(title,publishedAt,thumbnails(high(url))))&q=pokemon&type=video&order=date&relevanceLanguage=en-us&max-results=10&key=AIzaSyDlS5wq_ZDcOsHENER-5tsYPej6T3ziNT4&maxResults=25";        mAdapter = new VideoAdapter(getActivity(), linkListener);        setAdapter(mAdapter);        pokeNewsController = new PokeNewsController();        pokeNewsController.getPokeVideos(getActivity(), CONTENT_URL, super.newsListener);    }    @Override    protected void setAdapter(RecyclerView.Adapter adapter) {        super.mRecyclerView.setAdapter(adapter);    }    @Override    protected void updateAdapter(List<RssItem> items) {        mAdapter.update(items);    }    @Override    protected void onRestart() {        pokeNewsController.getPokeVideos(getActivity(), CONTENT_URL, newsListener);    }}